CREATE TABLE Ambito
  (
    id     int,

    code    char(3) not null,
    label   varchar(96)   not null,

    south  float   not null,
    north  float   not null,    
    west   float   not null,
    east   float   not null,

    UNIQUE(code),
    primary key(id)
  );

  
CREATE TABLE Sottoambito
  (
    id     int,

	ambcode    char(3) not null,
    code    int not null,	
    label   varchar(96)   not null,	

    south  float   not null,
    north  float   not null,    
    west   float   not null,
    east   float   not null,
    
    foreign key(ambcode) references Ambito(code),

    primary key(id)
  );




